}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 6px;
    height: 6px;
  }

  ::-webkit-scrollbar-track {
    background: hsl(var(--muted));
  }

  ::-webkit-scrollbar-thumb {
    background: hsl(var(--muted-foreground));
    border-radius: 3px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--border));
  }
}

@layer components {
  /* LeadPilot specific component styles */
  .leadpilot-gradient {
    background: linear-gradient(135deg, hsl(207, 90%, 54%) 0%, hsl(207, 90%, 48%) 100%);
  }

  .leadpilot-card {
    @apply bg-card rounded-xl shadow-sm border border-border hover:shadow-lg transition-shadow;
  }

  .leadpilot-btn-primary {
    @apply bg-primary text-primary-foreground hover:bg-primary/90 px-4 py-2 rounded-lg font-medium transition-colors;
  }

  .leadpilot-btn-secondary {
    @apply bg-secondary text-secondary-foreground hover:bg-secondary/80 px-4 py-2 rounded-lg font-medium transition-colors;
  }

  /* Status indicators */
  .status-indicator {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
  }

  .status-new {
    @apply bg-muted text-muted-foreground;
  }

  .status-contacted {
    @apply bg-blue-100 text-blue-800;
  }

  .status-qualified {
    @apply bg-emerald-100 text-emerald-800;
  }

  .status-converted {
    @apply bg-purple-100 text-purple-800;
  }

  /* Plan badges */
  .plan-badge {
    @apply inline-flex items-center px-2 py-1 rounded text-xs font-semibold;
  }

  .plan-free {
    @apply bg-muted text-muted-foreground;
  }

  .plan-starter {
    @apply bg-blue-100 text-blue-700;
  }

  .plan-pro {
    @apply bg-purple-100 text-purple-700;
  }

  .plan-growth {
    @apply bg-gradient-to-r from-purple-100 to-pink-100 text-purple-700;
  }

  /* Navigation */
  .nav-link {
    @apply flex items-center px-3 py-2 rounded-lg text-muted-foreground hover:bg-muted transition-colors;
  }

  .nav-link.active {
    @apply text-primary bg-primary/10;
  }

  /* Loading states */
  .loading-skeleton {
    @apply animate-pulse bg-muted rounded;
  }

  /* Form elements */
  .form-input {
    @apply w-full border border-border rounded-lg px-3 py-2 focus:ring-2 focus:ring-primary focus:border-transparent bg-input text-foreground;
  }

  .form-label {
    @apply block text-sm font-medium text-foreground mb-2;
  }

  /* Progress bars */
  .progress-bar {
    @apply w-full bg-muted rounded-full h-2;
  }

  .progress-fill {
    @apply h-2 rounded-full transition-all duration-300;
  }

  /* Stats cards */
  .stats-card {
    @apply bg-card p-6 rounded-xl shadow-sm border border-border;
  }

  .stats-icon {
    @apply w-12 h-12 rounded-lg flex items-center justify-center;
  }

  /* Template cards */
  .template-card {
    @apply bg-card rounded-xl shadow-sm border border-border p-6 hover:shadow-lg transition-shadow;
  }

  .template-card.locked {
    @apply opacity-60 cursor-not-allowed;
  }

  /* Campaign status */
  .campaign-status {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
  }

  .campaign-draft {
    @apply bg-muted text-muted-foreground;
  }

  .campaign-sent {
    @apply bg-emerald-100 text-emerald-800;
  }

  .campaign-scheduled {
    @apply bg-blue-100 text-blue-800;
  }

  /* Responsive utilities */
  @media (max-width: 768px) {
    .mobile-hidden {
      @apply hidden;
    }

    .mobile-full {
      @apply w-full;
    }
  }
}

/* Custom animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideIn {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-out;
}

.animate-slide-in {
  animation: slideIn 0.3s ease-out;
}

/* Focus states for accessibility */
.focus-ring {
  @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2;
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .print-break {
    page-break-before: always;
  }
}

