import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Plus, Trash2, Send, Clock } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import type { Campaign, Template, CustomEmail, Lead } from "@shared/schema";

export default function CampaignsSection() {
  const [campaignForm, setCampaignForm] = useState({
    name: "",
    emailId: "",
    selectedLeads: [] as string[],
    status: "draft"
  });

  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: campaigns = [], isLoading: campaignsLoading } = useQuery<Campaign[]>({
    queryKey: ["/api/campaigns"],
  });

  const { data: customEmails = [] } = useQuery<CustomEmail[]>({
    queryKey: ["/api/custom-emails"],
  });

  const { data: leads = [] } = useQuery<Lead[]>({
    queryKey: ["/api/leads"],
  });

  const createCampaignMutation = useMutation({
    mutationFn: async (campaignData: typeof campaignForm) => {
      const response = await apiRequest("POST", "/api/campaigns", campaignData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Campagne cr√©√©e",
        description: "Votre campagne a √©t√© cr√©√©e avec succ√®s.",
      });
      setCampaignForm({ name: "", emailId: "", selectedLeads: [], status: "draft" });
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Impossible de cr√©er la campagne.",
        variant: "destructive",
      });
    },
  });

  const sendCampaignMutation = useMutation({
    mutationFn: async (campaignId: string) => {
      const response = await apiRequest("POST", `/api/campaigns/${campaignId}/send`);
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Campagne envoy√©e !",
        description: `${data.results.sent} emails envoy√©s avec succ√®s.`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erreur d'envoi",
        description: error.message || "Impossible d'envoyer la campagne.",
        variant: "destructive",
      });
    },
  });

  const deleteCampaignMutation = useMutation({
    mutationFn: async (campaignId: string) => {
      const response = await apiRequest("DELETE", `/api/campaigns/${campaignId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/campaigns"] });
      toast({
        title: "Campagne supprim√©e",
        description: "La campagne a √©t√© supprim√©e avec succ√®s.",
      });
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Impossible de supprimer la campagne.",
        variant: "destructive",
      });
    },
  });

  const handleCreateCampaign = () => {
    if (!campaignForm.name || !campaignForm.emailId || campaignForm.selectedLeads.length === 0) {
      toast({
        title: "Erreur",
        description: "Veuillez remplir tous les champs et s√©lectionner au moins un lead.",
        variant: "destructive",
      });
      return;
    }

    const { selectedLeads, ...formData } = campaignForm;
    createCampaignMutation.mutate({
      ...formData,
      leadTargets: selectedLeads.join(','),
      status: 'draft'
    } as any);
  };

  const handleLeadSelection = (leadId: string, checked: boolean) => {
    if (checked) {
      setCampaignForm(prev => ({
        ...prev,
        selectedLeads: [...prev.selectedLeads, leadId]
      }));
    } else {
      setCampaignForm(prev => ({
        ...prev,
        selectedLeads: prev.selectedLeads.filter(id => id !== leadId)
      }));
    }
  };

  const handleSelectAllLeads = () => {
    setCampaignForm(prev => ({
      ...prev,
      selectedLeads: leads.map(lead => lead.id)
    }));
  };

  const handleSelectHighScoreLeads = () => {
    const highScoreLeads = leads.filter(lead => (lead.aiScore || 0) > 80);
    setCampaignForm(prev => ({
      ...prev,
      selectedLeads: highScoreLeads.map(lead => lead.id)
    }));
  };

  const handleDeselectAll = () => {
    setCampaignForm(prev => ({
      ...prev,
      selectedLeads: []
    }));
  };

  const handleViewCampaignDetails = (campaignId: string) => {
    // Pour l'instant, on affiche une alerte avec les d√©tails
    const campaign = campaigns.find(c => c.id === campaignId);
    if (campaign) {
      const details = `
D√©tails de la campagne: ${campaign.name}

‚Ä¢ Email utilis√©: ${customEmails.find((e: CustomEmail) => e.id === campaign.emailId)?.name || "Email inconnu"}
‚Ä¢ Leads cibl√©s: ${campaign.leadTargets ? campaign.leadTargets.split(',').length : 0} leads
‚Ä¢ Statut: ${campaign.status}
‚Ä¢ Cr√©√©e le: ${campaign.createdAt ? new Date(campaign.createdAt).toLocaleDateString('fr-FR') : 'Date inconnue'}

Statistiques:
‚Ä¢ Envoy√©s: ${campaign.totalSent}
‚Ä¢ Ouverts: ${campaign.totalOpened}
‚Ä¢ Clics: ${campaign.totalClicked}
‚Ä¢ R√©ponses: ${campaign.totalReplied}
      `.trim();
      alert(details);
    }
  };

  const handleDuplicateCampaign = (campaign: Campaign) => {
    setCampaignForm({
      name: `${campaign.name} (copie)`,
      emailId: campaign.emailId,
      selectedLeads: campaign.leadTargets ? campaign.leadTargets.split(',') : [],
      status: 'draft'
    });
    // Scroll vers le formulaire
    document.querySelector('.campaigns-form')?.scrollIntoView({ behavior: 'smooth' });
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      draft: { label: "Brouillon", variant: "secondary" as const },
      sent: { label: "Envoy√©e", variant: "default" as const },
      scheduled: { label: "Programm√©e", variant: "outline" as const },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.draft;
    return <Badge variant={config.variant}>{config.label}</Badge>;
  };

  if (campaignsLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-gray-200 rounded w-1/3"></div>
          <div className="h-32 bg-gray-200 rounded"></div>
          <div className="h-64 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-foreground">Campagnes Email</h2>
          <p className="text-muted-foreground">Cr√©ez et g√©rez vos campagnes d'emailing</p>
        </div>
        <Button>
          <Plus className="h-4 w-4 mr-2" />
          Nouvelle Campagne
        </Button>
      </div>

      {/* Campaign Creation Form */}
      <Card className="mb-6 campaigns-form">
        <CardHeader>
          <CardTitle>Cr√©er une Campagne</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            <div className="grid md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Nom de la campagne
                </label>
                <Input
                  type="text"
                  value={campaignForm.name}
                  onChange={(e) => setCampaignForm({ ...campaignForm, name: e.target.value })}
                  placeholder="Ex: Prospection Q1 2024"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-foreground mb-2">
                  Email
                </label>
                <Select 
                  value={campaignForm.emailId} 
                  onValueChange={(value) => setCampaignForm({ ...campaignForm, emailId: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="S√©lectionner un email" />
                  </SelectTrigger>
                  <SelectContent>
                    {customEmails.map((email: CustomEmail) => (
                      <SelectItem key={email.id} value={email.id}>
                        {email.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-foreground mb-3">
                S√©lection des leads cibles
              </label>

              {/* Actions rapides */}
              <div className="flex flex-wrap gap-2 mb-4">
                <Button 
                  type="button" 
                  variant="outline" 
                  size="sm"
                  onClick={handleSelectAllLeads}
                  className="bg-card hover:bg-muted dark:bg-card dark:hover:bg-muted"
                >
                  ‚úì Tous les leads ({leads.length})
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  size="sm"
                  onClick={handleSelectHighScoreLeads}
                  className="bg-card hover:bg-muted dark:bg-card dark:hover:bg-muted"
                >
                  ‚≠ê Score {'>'} 80% ({leads.filter(lead => (lead.aiScore || 0) > 80).length})
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  size="sm"
                  onClick={handleDeselectAll}
                  className="bg-card hover:bg-muted dark:bg-card dark:hover:bg-muted"
                >
                  ‚úó D√©s√©lectionner tout
                </Button>
                <div className="ml-auto bg-primary/10 px-3 py-1 rounded-full text-sm font-medium text-primary">
                  {campaignForm.selectedLeads.length} s√©lectionn√©s
                </div>
              </div>

              {/* Liste des leads avec cases √† cocher */}
              <div className="border rounded-lg bg-card dark:bg-card">
                <div className="p-3 bg-muted dark:bg-muted border-b font-medium text-sm text-foreground">
                  Vos leads disponibles
                </div>
                <div className="max-h-64 overflow-y-auto">
                  {leads.length === 0 ? (
                    <div className="p-4 text-center text-muted-foreground">
                      <p>Aucun lead disponible</p>
                      <p className="text-xs mt-1">Ajoutez des leads dans l'onglet "Leads" pour cr√©er une campagne</p>
                    </div>
                  ) : (
                    <div className="divide-y divide-border dark:divide-border">
                      {leads.map((lead: Lead) => (
                        <div key={lead.id} className="p-3 hover:bg-muted dark:hover:bg-muted transition-colors">
                          <div className="flex items-center space-x-3">
                            <Checkbox
                              id={`lead-${lead.id}`}
                              checked={campaignForm.selectedLeads.includes(lead.id)}
                              onCheckedChange={(checked) => handleLeadSelection(lead.id, checked as boolean)}
                              className="flex-shrink-0"
                            />
                            <div className="flex-1 min-w-0">
                              <div className="flex items-center justify-between">
                                <div>
                                  <h4 className="font-medium text-foreground text-sm">
                                    {lead.firstName} {lead.lastName}
                                  </h4>
                                  <div className="flex items-center space-x-2 text-xs text-muted-foreground mt-1">
                                    <span className="font-medium">{lead.company}</span>
                                    {lead.position && (
                                      <>
                                        <span>‚Ä¢</span>
                                        <span>{lead.position}</span>
                                      </>
                                    )}
                                  </div>
                                  <div className="text-xs text-muted-foreground mt-1">
                                    {lead.email}
                                  </div>
                                </div>
                                <div className="flex items-center space-x-2">
                                  {lead.aiScore && (
                                    <Badge 
                                      variant={lead.aiScore > 80 ? "default" : lead.aiScore > 60 ? "secondary" : "outline"}
                                      className="text-xs"
                                    >
                                      {Math.round(lead.aiScore)}%
                                    </Badge>
                                  )}
                                  <Badge variant="outline" className="text-xs">
                                    {lead.status || 'nouveau'}
                                  </Badge>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>

          </div>
          <div className="flex justify-end mt-6">
            <Button 
              onClick={handleCreateCampaign}
              disabled={createCampaignMutation.isPending}
            >
              {createCampaignMutation.isPending ? "Cr√©ation..." : "Cr√©er Campagne"}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Campaigns List */}
      <Card>
        <CardHeader>
          <CardTitle>Campagnes R√©centes</CardTitle>
        </CardHeader>
        <CardContent>
          {campaigns.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-muted-foreground mb-4">Aucune campagne cr√©√©e pour le moment.</p>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                Cr√©er votre premi√®re campagne
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {campaigns.map((campaign: Campaign) => (
                <div key={campaign.id} className="border border-border rounded-lg p-4 bg-card">
                  <div className="flex items-center justify-between mb-3">
                    <div>
                      <h4 className="text-lg font-medium text-foreground">{campaign.name}</h4>
                      <p className="text-sm text-muted-foreground">
                        Email: {customEmails.find((e: CustomEmail) => e.id === campaign.emailId)?.name || "Email inconnu"}
                      </p>
                    </div>
                    <div className="flex items-center space-x-2">
                      {getStatusBadge(campaign.status)}
                      {campaign.status === 'draft' && (
                        <Button
                          size="sm"
                          onClick={() => sendCampaignMutation.mutate(campaign.id)}
                          disabled={sendCampaignMutation.isPending}
                          className="bg-green-600 hover:bg-green-700 text-white"
                        >
                          {sendCampaignMutation.isPending ? (
                            <Clock className="h-4 w-4 mr-2 animate-spin" />
                          ) : (
                            <Send className="h-4 w-4 mr-2" />
                          )}
                          Envoyer
                        </Button>
                      )}
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="ghost" size="sm" className="text-red-600 hover:text-red-700">
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Supprimer la campagne</AlertDialogTitle>
                            <AlertDialogDescription>
                              √ätes-vous s√ªr de vouloir supprimer la campagne "{campaign.name}" ? 
                              Cette action est irr√©versible et supprimera toutes les donn√©es associ√©es.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Annuler</AlertDialogCancel>
                            <AlertDialogAction 
                              onClick={() => deleteCampaignMutation.mutate(campaign.id)}
                              className="bg-red-600 hover:bg-red-700"
                            >
                              Supprimer
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>
                  <div className="grid grid-cols-4 gap-4 mb-3">
                    <div className="text-center">
                      <div className="text-2xl font-bold text-foreground">{campaign.totalSent}</div>
                      <div className="text-sm text-muted-foreground">Envoy√©s</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-emerald-400">{campaign.totalOpened}</div>
                      <div className="text-sm text-muted-foreground">Ouverts</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-blue-400">{campaign.totalClicked}</div>
                      <div className="text-sm text-muted-foreground">Clics</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-purple-400">{campaign.totalReplied}</div>
                      <div className="text-sm text-muted-foreground">R√©ponses</div>
                    </div>
                  </div>
                  <div className="flex justify-between items-center text-sm text-muted-foreground">
                    <span>Cr√©√©e le {campaign.createdAt ? new Date(campaign.createdAt).toLocaleDateString('fr-FR') : 'Date inconnue'}</span>
                    <div className="space-x-3">
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => handleViewCampaignDetails(campaign.id)}
                        className="text-foreground hover:text-primary"
                      >
                        Voir d√©tails
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => handleDuplicateCampaign(campaign)}
                        className="text-foreground hover:text-primary"
                      >
                        Dupliquer
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

