                  if (sequenceSteps.length < maxSteps) {
                    setSequenceSteps(prev => [...prev, {
                      stepNumber: prev.length + 1,
                      name: `√âtape ${prev.length + 1}`,
                      delayDays: prev.length === 0 ? 0 : 3,
                      delayHours: 0,
                      isActive: true
                    }]);
                  }
                }}
                disabled={sequenceSteps.length >= maxSteps}
                variant="outline"
              >
                <Plus className="h-4 w-4 mr-2" />
                Ajouter une √©tape
              </Button>
            </div>

            {/* Liste des √©tapes configurables */}
            <div className="space-y-4">
              {sequenceSteps.map((step, index) => (
                <div key={index} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center text-sm font-medium">
                        {step.stepNumber}
                      </div>
                      <Input
                        value={step.name}
                        onChange={(e) => {
                          setSequenceSteps(prev => prev.map((s, i) => 
                            i === index ? { ...s, name: e.target.value } : s
                          ));
                        }}
                        placeholder="Nom de l'√©tape"
                        className="max-w-xs"
                      />
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        setSequenceSteps(prev => prev.filter((_, i) => i !== index));
                      }}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {/* S√©lection de l'email */}
                    <div>
                      <label className="text-sm font-medium">Email √† envoyer</label>
                      <Select 
                        value={step.emailId || ""}
                        onValueChange={(value) => {
                          setSequenceSteps(prev => prev.map((s, i) => 
                            i === index ? { ...s, emailId: value } : s
                          ));
                        }}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Choisir un email" />
                        </SelectTrigger>
                        <SelectContent>
                          {customEmails.length === 0 ? (
                            <SelectItem value="none" disabled>
                              Aucun email personnalis√©
                            </SelectItem>
                          ) : (
                            customEmails.map((email: any) => (
                              <SelectItem key={email.id} value={email.id}>
                                {email.name}
                              </SelectItem>
                            ))
                          )}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* D√©lai en jours */}
                    <div>
                      <label className="text-sm font-medium mb-2 block">
                        D√©lai {index === 0 ? "(envoi imm√©diat)" : "(apr√®s √©tape pr√©c√©dente)"}
                      </label>
                      <div className="flex items-center space-x-2">
                        <Input
                          type="number"
                          min="0"
                          value={step.delayDays}
                          onChange={(e) => {
                            setSequenceSteps(prev => prev.map((s, i) => 
                              i === index ? { ...s, delayDays: parseInt(e.target.value) || 0 } : s
                            ));
                          }}
                          disabled={index === 0}
                          className="w-16"
                        />
                        <span className="text-xs text-muted-foreground min-w-[28px]">jours</span>
                        <Input
                          type="number"
                          min="0"
                          max="23"
                          value={step.delayHours}
                          onChange={(e) => {
                            setSequenceSteps(prev => prev.map((s, i) => 
                              i === index ? { ...s, delayHours: parseInt(e.target.value) || 0 } : s
                            ));
                          }}
                          disabled={index === 0}
                          className="w-16"
                        />
                        <span className="text-xs text-muted-foreground">heures</span>
                      </div>
                    </div>

                    {/* Statut */}
                    <div>
                      <label className="text-sm font-medium mb-2 block">Statut</label>
                      <div className="flex items-center">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            setSequenceSteps(prev => prev.map((s, i) => 
                              i === index ? { ...s, isActive: !s.isActive } : s
                            ));
                          }}
                          className={`${step.isActive ? 'bg-green-50 border-green-200 text-green-700' : 'bg-gray-50 border-gray-200 text-gray-500'}`}
                        >
                          {step.isActive ? "Active" : "Inactive"}
                        </Button>
                      </div>
                    </div>
                  </div>

                  {/* Aper√ßu du timing */}
                  <div className="mt-3 p-3 bg-gray-50 rounded text-sm">
                    <Clock className="h-4 w-4 inline mr-2" />
                    {index === 0 
                      ? "Envoy√© imm√©diatement √† l'inscription dans la s√©quence"
                      : `Envoy√© ${step.delayDays} jour(s) ${step.delayHours > 0 ? `et ${step.delayHours} heure(s)` : ''} apr√®s l'√©tape pr√©c√©dente si pas de r√©ponse`
                    }
                  </div>
                </div>
              ))}
            </div>

            {sequenceSteps.length === 0 && (
              <div className="text-center py-6 border-2 border-dashed border-gray-200 rounded-lg bg-gray-50">
                <Mail className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                <p className="text-gray-500">Aucune √©tape configur√©e</p>
                <p className="text-xs text-gray-400">Cliquez sur "Ajouter une √©tape" pour commencer</p>
              </div>
            )}

            {/* Actions */}
            <div className="flex justify-end space-x-2 pt-4 border-t">
              <Button variant="outline" onClick={() => setIsEditingSteps(false)}>
                Annuler
              </Button>
              <Button 
                onClick={async () => {
                  if (selectedSequence && sequenceConfig.selectedLeads.length > 0) {
                    try {
                      await apiRequest("POST", `/api/sequences/${selectedSequence}/steps`, { 
                        steps: sequenceSteps,
                        selectedLeads: sequenceConfig.selectedLeads 
                      });
                      setIsEditingSteps(false);
                      toast({
                        title: "S√©quence configur√©e",
                        description: `${sequenceSteps.length} √©tape(s) configur√©e(s) pour ${sequenceConfig.selectedLeads.length} lead(s)`,
                      });
                    } catch (error) {
                      toast({
                        title: "Erreur",
                        description: "Impossible de sauvegarder la configuration",
                        variant: "destructive",
                      });
                    }
                  }
                }}
                disabled={sequenceSteps.length === 0 || sequenceConfig.selectedLeads.length === 0}
              >
                Sauvegarder la s√©quence
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Liste des s√©quences existantes */}
      {sequences.length > 0 ? (
        <div className="grid gap-6">
          {/* Statistiques */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <Card>
              <CardContent className="flex items-center p-4">
                <div className="p-2 bg-blue-100 rounded-lg mr-3">
                  <Mail className="h-4 w-4 text-blue-600" />
                </div>
                <div className="min-w-0 flex-1">
                  <p className="text-xs font-medium text-muted-foreground truncate">S√©quences Actives</p>
                  <p className="text-xl font-bold text-foreground">{sequences.filter((s: any) => s.isActive).length}</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="flex items-center p-4">
                <div className="p-2 bg-green-100 rounded-lg mr-3">
                  <Users className="h-4 w-4 text-green-600" />
                </div>
                <div className="min-w-0 flex-1">
                  <p className="text-xs font-medium text-muted-foreground truncate">Leads Inscrits</p>
                  <p className="text-xl font-bold text-foreground">0</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="flex items-center p-4">
                <div className="p-2 bg-purple-100 rounded-lg mr-3">
                  <Clock className="h-4 w-4 text-purple-600" />
                </div>
                <div className="min-w-0 flex-1">
                  <p className="text-xs font-medium text-muted-foreground truncate">Emails Planifi√©s</p>
                  <p className="text-xl font-bold text-foreground">0</p>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="flex items-center p-4">
                <div className="p-2 bg-orange-100 rounded-lg mr-3">
                  <TrendingUp className="h-4 w-4 text-orange-600" />
                </div>
                <div className="min-w-0 flex-1">
                  <p className="text-xs font-medium text-muted-foreground truncate">Taux de R√©ponse</p>
                  <p className="text-xl font-bold text-foreground">--</p>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Liste des s√©quences */}
          <Card>
            <CardHeader>
              <CardTitle>Mes S√©quences</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {sequences.map((sequence: any) => (
                  <div key={sequence.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex items-center space-x-4">
                      <div className={`w-2 h-2 rounded-full ${sequence.isActive ? 'bg-green-500' : 'bg-gray-400'}`}></div>
                      <div>
                        <h4 className="font-medium text-foreground">{sequence.name}</h4>
                        <p className="text-sm text-muted-foreground">{sequence.description || "Aucune description"}</p>
                        <p className="text-xs text-muted-foreground mt-1">0 √©tapes configur√©es ‚Ä¢ 0 leads inscrits</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Badge variant="outline" className={sequence.isActive ? "bg-green-50 text-green-700" : "bg-gray-50 text-gray-600"}>
                        {sequence.isActive ? "Active" : "Inactive"}
                      </Badge>
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => {
                          setSelectedSequence(sequence.id);
                          setIsEditingSteps(true);
                          setSequenceSteps([]);
                          setSequenceConfig({ selectedLeads: [] });
                        }}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => {
                          toggleSequenceMutation.mutate({
                            sequenceId: sequence.id,
                            isActive: !sequence.isActive
                          });
                        }}
                        disabled={toggleSequenceMutation.isPending}
                      >
                        {sequence.isActive ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => {
                          if (window.confirm("√ätes-vous s√ªr de vouloir supprimer cette s√©quence ?")) {
                            deleteSequenceMutation.mutate(sequence.id);
                          }
                        }}
                        disabled={deleteSequenceMutation.isPending}
                        className="text-red-600 hover:text-red-700 hover:bg-red-50"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>
      ) : (
        <Card className="border-2 border-dashed border-muted">
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Mail className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-xl font-semibold text-foreground mb-2">
              Aucune s√©quence cr√©√©e
            </h3>
            <p className="text-muted-foreground text-center mb-6">
              Cr√©ez votre premi√®re s√©quence automatis√©e pour commencer √† nurturing vos prospects avec des emails programm√©s.
            </p>
            <Button
              onClick={() => setIsCreateDialogOpen(true)}
              className="bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
            >
              <Plus className="h-4 w-4 mr-2" />
              Cr√©er ma premi√®re s√©quence
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

