import type { CustomEmail } from "@shared/schema";

interface CustomEmailsSectionProps {
  onSectionChange?: (section: string) => void;
}

export default function CustomEmailsSection({ onSectionChange }: CustomEmailsSectionProps) {
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();

  // V√©rification du plan pour le booking
  const userPlan = (user as any)?.plan || "free";
  const canUseBooking = ['starter', 'pro', 'growth'].includes(userPlan);
  const [viewingEmail, setViewingEmail] = useState<CustomEmail | null>(null);
  const [editingEmail, setEditingEmail] = useState<CustomEmail | null>(null);
  const [editedName, setEditedName] = useState("");
  const [editedSubject, setEditedSubject] = useState("");
  const [editedContent, setEditedContent] = useState("");
  const [originalContent, setOriginalContent] = useState("");
  const [generatingVariation, setGeneratingVariation] = useState(false);
  const [lastGeneratedContent, setLastGeneratedContent] = useState("");

  // Fonction pour ajouter le lien de booking
  const handleAddBookingLink = () => {
    if (!canUseBooking) {
      toast({
        title: "Fonctionnalit√© r√©serv√©e",
        description: "Les liens de RDV sont disponibles √† partir du plan Starter.",
        variant: "destructive",
      });
      return;
    }

    const userId = (user as any)?.id;
    const bookingText = `\n\nPour programmer un RDV, cliquez ici :\nhttps://leadpilot.com/book/${userId}\n\nOu r√©pondez √† cet email pour convenir d'un autre cr√©neau.`;

    // Ajouter le texte √† la fin du contenu existant
    setEditedContent(prevContent => prevContent + bookingText);

    toast({
      title: "Lien de RDV ajout√© !",
      description: "Le texte de proposition de rendez-vous a √©t√© ins√©r√© √† la fin de votre email.",
    });
  };

  const { data: customEmails = [], isLoading } = useQuery<CustomEmail[]>({
    queryKey: ["/api/custom-emails"],
  });

  const deleteEmailMutation = useMutation({
    mutationFn: async (emailId: string) => {
      const response = await apiRequest("DELETE", `/api/custom-emails/${emailId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/custom-emails"] });
      toast({
        title: "Email supprim√©",
        description: "L'email personnalis√© a √©t√© supprim√© avec succ√®s.",
      });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de supprimer l'email.",
        variant: "destructive",
      });
    },
  });

  const updateEmailMutation = useMutation({
    mutationFn: async (data: { id: string; name: string; subject: string; content: string }) => {
      const response = await apiRequest("PATCH", `/api/custom-emails/${data.id}`, {
        name: data.name,
        subject: data.subject,
        content: data.content,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/custom-emails"] });
      setEditingEmail(null);
      toast({
        title: "Email mis √† jour",
        description: "L'email personnalis√© a √©t√© modifi√© avec succ√®s.",
      });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Impossible de modifier l'email.",
        variant: "destructive",
      });
    },
  });

  const handleCopyEmail = (customEmail: CustomEmail) => {
    const emailText = `Objet: ${customEmail.subject}\n\nContenu:\n${customEmail.content}`;
    navigator.clipboard.writeText(emailText);
    toast({
      title: "Email copi√© !",
