import type { Express } from "express";
import { isAuthenticated } from "../replitAuth";

// Donn√©es simul√©es pour les s√©quences
const mockSequences: Record<string, any[]> = {
  "45880930": [
    {
      id: "seq-1",
      userId: "45880930",
      name: "Prospection initiale",
      description: "Premi√®re approche des prospects",
      isActive: true,
      createdAt: new Date(),
      steps: []
    }
  ]
};

export function registerSequenceRoutes(app: Express) {
  // R√©cup√©rer les s√©quences d'un utilisateur
  app.get('/api/sequences', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Retourner les s√©quences pour cet utilisateur
      const userSequences = mockSequences[userId] || [];

      res.json(userSequences);
    } catch (error) {
      console.error("Error fetching sequences:", error);
      res.status(500).json({ message: "Failed to fetch sequences" });
    }
  });

  // Cr√©er une nouvelle s√©quence
  app.post('/api/sequences', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { name, description } = req.body;

      if (!name) {
        return res.status(400).json({ message: "Name is required" });
      }

      console.log(`üìß Nouvelle s√©quence cr√©√©e par ${userId}: ${name}`);

      // Pour l'instant, retourner une r√©ponse mock
      const newSequence = {
        id: `seq-${Date.now()}`,
        userId,
        name,
        description: description || "",
        isActive: true,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };

      res.json(newSequence);
    } catch (error) {
      console.error("Error creating sequence:", error);
      res.status(500).json({ message: "Failed to create sequence" });
    }
  });

  // Cr√©er/mettre √† jour les √©tapes d'une s√©quence
  app.post('/api/sequences/:id/steps', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { steps } = req.body;
      const userId = req.user.claims.sub;

      console.log(`üìß Configuration des √©tapes pour la s√©quence ${id}:`, steps.length, '√©tapes');

      // Simulation de la sauvegarde des √©tapes
      // En production, ici on sauvegarderait en base de donn√©es

      // Programmer les envois automatiques pour les √©tapes configur√©es
      steps.forEach((step: any, index: number) => {
        if (step.emailId && step.delayDays >= 0) {
          console.log(`  ‚è∞ √âtape ${index + 1}: ${step.name} - D√©lai: ${step.delayDays}j ${step.delayHours}h`);
          console.log(`     Email: ${step.emailId}`);
        }
      });

      res.json({ 
        message: "Steps configured successfully", 
        stepsCount: steps.length,
        automationEnabled: true
      });
    } catch (error) {
      console.error("Error configuring sequence steps:", error);
      res.status(500).json({ message: "Failed to configure steps" });
    }
  });

  // Inscrire des leads dans une s√©quence
  app.post('/api/sequences/:id/enroll', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { leadIds, steps } = req.body;
      const userId = req.user.claims.sub;

      console.log(`üìß Inscription de ${leadIds.length} lead(s) dans la s√©quence ${id}`);

      const { SequenceAutomationService } = await import("../services/sequenceAutomation");

      const enrollmentResults = [];

      for (const leadId of leadIds) {
        const scheduledEmails = await SequenceAutomationService.enrollLeadInSequence(
          id, leadId, userId, steps
        );
        enrollmentResults.push({
          leadId,
          scheduledEmails: scheduledEmails.length
        });
      }

      res.json({ 
        message: "Leads enrolled successfully",
        enrollments: enrollmentResults.length,
        totalScheduledEmails: enrollmentResults.reduce((sum, r) => sum + r.scheduledEmails, 0)
      });
    } catch (error) {
      console.error("Error enrolling leads:", error);
      res.status(500).json({ message: "Failed to enroll leads" });
    }
  });

  // Statistiques de l'automatisation
  app.get('/api/sequences/automation/stats', isAuthenticated, async (req: any, res) => {
    try {
      const { SequenceAutomationService } = await import("../services/sequenceAutomation");
      const stats = SequenceAutomationService.getQueueStats();

      res.json(stats);
    } catch (error) {
      console.error("Error getting automation stats:", error);
      res.status(500).json({ message: "Failed to get stats" });
    }
  });

  // Supprimer une s√©quence
  app.delete('/api/sequences/:id', async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = "45880930"; // Utilisateur fixe pour la d√©mo

      console.log(`üóëÔ∏è Suppression de la s√©quence ${id} pour l'utilisateur ${userId}`);

      // V√©rifier si l'utilisateur a des s√©quences
      if (!mockSequences[userId]) {
        mockSequences[userId] = [];
      }

      // Supprimer la s√©quence des donn√©es simul√©es
      const initialCount = mockSequences[userId].length;
      mockSequences[userId] = mockSequences[userId].filter(seq => seq.id !== id);
      const finalCount = mockSequences[userId].length;
      console.log(`‚úÖ S√©quence ${id} supprim√©e avec succ√®s (${initialCount} -> ${finalCount})`);

      res.json({ message: "Sequence deleted successfully", sequenceId: id });
    } catch (error) {
      console.error("Error deleting sequence:", error);
      res.status(500).json({ message: "Failed to delete sequence" });
    }
  });

  // Toggle statut d'une s√©quence
  app.patch('/api/sequences/:id/toggle', async (req: any, res) => {
    try {
      const { id } = req.params;
      const { isActive } = req.body;
      const userId = "45880930"; // Utilisateur fixe pour la d√©mo

      console.log(`üîÑ Toggle s√©quence ${id} pour l'utilisateur ${userId}: ${isActive ? 'Active' : 'Inactive'}`);

      // V√©rifier si l'utilisateur a des s√©quences
      if (!mockSequences[userId]) {
        mockSequences[userId] = [];
      }

      // Mettre √† jour le statut de la s√©quence dans les donn√©es simul√©es
      const sequenceIndex = mockSequences[userId].findIndex(seq => seq.id === id);
      if (sequenceIndex !== -1) {
        mockSequences[userId][sequenceIndex].isActive = isActive;
        console.log(`‚úÖ Statut mis √† jour: ${isActive ? 'Active' : 'Inactive'} pour s√©quence ${id}`);
      } else {
        console.log(`‚ùå S√©quence ${id} non trouv√©e dans les donn√©es`);
      }

      res.json({ 
        message: "Sequence status updated successfully", 
        sequenceId: id,
        isActive: isActive 
      });
    } catch (error) {
      console.error("Error toggling sequence:", error);
      res.status(500).json({ message: "Failed to toggle sequence status" });
    }
  });
}

server/services/apolloService.ts.content:
import type { LeadGenerationService, LeadSearchParams, GeneratedLead, EnrichedLeadData } from "./leadService";

export class ApolloLeadService implements LeadGenerationService {
  private apiKey: string;
  private baseUrl = "https://api.apollo.io/v1";

  constructor() {
    this.apiKey = process.env.APOLLO_API_KEY || "";
    if (!this.apiKey) {
      throw new Error("APOLLO_API_KEY is required");
    }
  }

  async generateLeads(params: LeadSearchParams): Promise<GeneratedLead[]> {
    try {
      const searchParams = this.buildSearchParams(params);

      const response = await fetch(`${this.baseUrl}/mixed_people/search`, {
        method: 'POST',
        headers: {
          'Cache-Control': 'no-cache',
          'Content-Type': 'application/json',
          'X-Api-Key': this.apiKey
        },
        body: JSON.stringify(searchParams)
      });

      if (!response.ok) {
        throw new Error(`Apollo API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      return this.transformApolloResponse(data);
    } catch (error) {
      console.error("Error calling Apollo API:", error);
      throw error;
    }
  }

  async enrichLead(email: string): Promise<EnrichedLeadData | null> {
    try {
      const response = await fetch(`${this.baseUrl}/people/match`, {
        method: 'POST',
        headers: {
          'Cache-Control': 'no-cache',
          'Content-Type': 'application/json',
          'X-Api-Key': this.apiKey
        },
        body: JSON.stringify({
          email: email,
          reveal_personal_emails: true
        })
      });

      if (!response.ok) {
        if (response.status === 404) {
          return null; // Person not found
        }
        throw new Error(`Apollo enrichment error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      return this.transformEnrichmentResponse(data);
    } catch (error) {
      console.error("Error enriching lead with Apollo:", error);
      return null;
    }
  }

  private buildSearchParams(params: LeadSearchParams) {
    const apolloParams: any = {
      page: 1,
      per_page: Math.min(params.limit || 25, 100),
      person_locations: params.location ? [params.location] : ["France"],
      person_seniorities: ["c_suite", "senior", "director", "vp"],
    };

    if (params.sector) {
      // Map common sectors to Apollo industry keywords
      const industryMapping: Record<string, string[]> = {
        "Tech/SaaS": ["Software", "Computer Software", "Information Technology", "SaaS"],
        "E-commerce": ["E-commerce", "Retail", "Internet"],
        "Finance": ["Financial Services", "Banking", "Fintech"],
        "Marketing": ["Marketing", "Advertising", "Digital Marketing"],
        "Conseil": ["Management Consulting", "Business Consulting"],
        "Sant√©": ["Healthcare", "Medical Devices", "Biotechnology"],
        "Education": ["Education", "E-learning", "EdTech"]
      };

      const industries = industryMapping[params.sector] || [params.sector];
      apolloParams.organization_industry_tag_ids = industries;
    }

    if (params.jobTitles && params.jobTitles.length > 0) {
      apolloParams.person_titles = params.jobTitles;
    } else {
      // Default to decision maker titles
      apolloParams.person_titles = [
        "CEO", "CTO", "CMO", "COO", "Founder", "Co-founder",
        "Director", "VP", "Head of", "Manager"
      ];
    }

    if (params.companySize) {
      const sizeMapping: Record<string, string[]> = {
        "startup": ["1-10", "11-50"],
        "small": ["11-50", "51-200"],
        "medium": ["201-500", "501-1000"],
        "large": ["1001-5000", "5001+"]
      };
      apolloParams.organization_num_employees_ranges = sizeMapping[params.companySize] || ["11-200"];
    }

    return apolloParams;
  }

  private transformApolloResponse(data: any): GeneratedLead[] {
    if (!data.people || !Array.isArray(data.people)) {
      return [];
    }

    return data.people.map((person: any) => {
      const organization = person.organization || {};

      return {
        firstName: person.first_name || "",
        lastName: person.last_name || "",
        email: person.email || "",
        company: organization.name || "",
        sector: this.mapApolloIndustryToSector(organization.industry),
        position: person.title || "",
        linkedinUrl: person.linkedin_url || "",
        companyWebsite: organization.website_url || "",
        employeeCount: organization.estimated_num_employees || 0,
        location: person.city && person.state ? `${person.city}, ${person.state}` : person.country || ""
      };
    });
  }

  private transformEnrichmentResponse(data: any): EnrichedLeadData {
    const person = data.person || {};
    const organization = person.organization || {};

    return {
      company: {
        industry: organization.industry || "",
        size: this.mapEmployeeCountToSize(organization.estimated_num_employees),
        revenue: organization.estimated_annual_revenue || "",
        founded: organization.founded_year || 0,
        technologies: organization.technologies || []
      },
      person: {
        seniority: this.mapTitleToSeniority(person.title),
        department: this.mapTitleToDepartment(person.title),
        phoneNumber: person.phone_numbers?.[0]?.sanitized_number || ""
      },
      socialProfiles: {
        linkedin: person.linkedin_url || "",
        twitter: person.twitter_url || ""
      }
    };
  }

  private mapApolloIndustryToSector(industry: string): string {
    if (!industry) return "Non sp√©cifi√©";

    const mapping: Record<string, string> = {
      "Computer Software": "Tech/SaaS",
      "Software": "Tech/SaaS",
      "Information Technology": "Tech/SaaS",
      "SaaS": "Tech/SaaS",
      "Internet": "E-commerce",
      "E-commerce": "E-commerce",
      "Retail": "E-commerce",
      "Financial Services": "Finance",
      "Banking": "Finance",
      "Fintech": "Finance",
      "Marketing": "Marketing",
      "Advertising": "Marketing",
      "Digital Marketing": "Marketing",
      "Management Consulting": "Conseil",
      "Business Consulting": "Conseil",
      "Healthcare": "Sant√©",
      "Medical Devices": "Sant√©",
      "Biotechnology": "Sant√©",
      "Education": "Education",
      "E-learning": "Education",
      "EdTech": "Education"
    };

    return mapping[industry] || industry;
  }

  private mapEmployeeCountToSize(count: number): string {
    if (count <= 10) return "1-10";
    if (count <= 50) return "11-50";
    if (count <= 200) return "51-200";
    if (count <= 500) return "201-500";
    return "500+";
  }

  private mapTitleToSeniority(title: string): string {
    if (!title) return "Mid";

    const titleLower = title.toLowerCase();
    if (titleLower.includes("ceo") || titleLower.includes("founder") || titleLower.includes("president")) {
      return "Executive";
    }
    if (titleLower.includes("cto") || titleLower.includes("cmo") || titleLower.includes("coo") || titleLower.includes("vp")) {
      return "Executive";
    }
    if (titleLower.includes("director") || titleLower.includes("head")) {
      return "Senior";
    }
    if (titleLower.includes("manager") || titleLower.includes("lead")) {
      return "Mid";
    }
    if (titleLower.includes("junior") || titleLower.includes("associate") || titleLower.includes("coordinator")) {
      return "Junior";
    }

    return "Mid";
  }

  private mapTitleToDepartment(title: string): string {
    if (!title) return "Operations";

    const titleLower = title.toLowerCase();
    if (titleLower.includes("sales") || titleLower.includes("business development") || titleLower.includes("revenue")) {
      return "Sales";
    }
    if (titleLower.includes("marketing") || titleLower.includes("growth") || titleLower.includes("content")) {
      return "Marketing";
    }
    if (titleLower.includes("tech") || titleLower.includes("engineer") || titleLower.includes("developer") || titleLower.includes("cto")) {
      return "Technology";
    }
    if (titleLower.includes("finance") || titleLower.includes("accounting") || titleLower.includes("cfo")) {
      return "Finance";
    }
    if (titleLower.includes("hr") || titleLower.includes("people") || titleLower.includes("talent")) {
      return "HR";
    }
    if (titleLower.includes("operations") || titleLower.includes("ops") || titleLower.includes("coo")) {
      return "Operations";
    }

    return "Operations";
  }
}

